// For Full screen Mode
function updateButtonIcon() {
    const icon = $('#fullscreen-icon');
    if (icon.length) { // Check if the icon element exists
        if (document.fullscreenElement) {
            icon.attr('src', 'https://gahshomar.com/wp-content/uploads/2024/08/exit-door-svgrepo-com.svg');
            icon.attr('alt', 'Exit Fullscreen');
        } else {
            icon.attr('src', 'https://gahshomar.com/wp-content/uploads/2024/08/full-screen-circle-svgrepo-com.svg');
            icon.attr('alt', 'Go Fullscreen');
        }
    }
}

jQuery(document).ready(function($) {
    const fullscreenButton = $('#fullscreen-button');

    if (fullscreenButton.length) { // Check if the button element exists
        fullscreenButton.on('click', function() {
            if (!document.fullscreenElement) {
                document.documentElement.requestFullscreen().catch((err) => {
                    console.log(`Error attempting to enable full-screen mode: ${err.message} (${err.name})`);
                });
            } else {
                document.exitFullscreen();
            }
        });

        // Update button icon when fullscreen state changes
        $(document).on('fullscreenchange', updateButtonIcon);

        // Initial button icon update
        updateButtonIcon();
    } else {
        console.log('Fullscreen button not found.');
    }
});